@section('script-socket')
    <script>
        const socketClient = io.connect("127.0.0.1:33399")
        @if($ownerLive->id === auth()->id())
        navigator.mediaDevices.getUserMedia({ video: true, audio: false })
            .then((stream) => {
                const video = document.getElementById('video');
                video.srcObject = stream;

                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');

                setInterval(() => {
                    context.drawImage(video, 0, 0, canvas.width, canvas.height);
                    const imageData = canvas.toDataURL('image/jpeg');
                    socketClient.emit('stream', imageData, {{ $ownerLive->id }});
                }, 1000 / 30);
            })
            .catch((error) => {
                console.error('Error accessing webcam:', error);
            });
        @else
            socketClient.emit('client-join-video-room', {{ $ownerLive->id }})
            const img = document.getElementById('stream')
            socketClient.on('stream', (data) => {
                img.src = data;
            });
        @endif
    </script>
@endsection